package main

import (
	"log"

	"github.com/vulpemventures/go-elements/block"
)

var regtestGenesis = ""

func main() {
	geneBlock, err := block.NewFromHex(regtestGenesis)
	if err != nil {
		panic(err)
	}

	log.Println("prev block", geneBlock.Header.PrevBlockHash)
	log.Println("vers", geneBlock.Header.Version)
	log.Println(geneBlock.Header.Height)
	log.Println(geneBlock.Header.MerkleRoot)
	log.Println(geneBlock.Header.Timestamp)
	log.Println("")
	log.Println(geneBlock.Header.ExtData.IsDyna)
	log.Println(geneBlock.Header.ExtData.DynamicFederation)
	log.Println(geneBlock.Header.ExtData.Proof.Challenge)
	log.Println(geneBlock.Header.ExtData.Proof.Solution)

	log.Println(geneBlock.Header)

	h, err := geneBlock.Header.Hash()
	if err != nil {
		panic(err)
	}

	log.Println(h)
}
